import React, { Component } from 'react'
import $ from 'jquery';
import jwt from 'jwt-decode';
import './style.css'
import { useState } from 'react'


function Authentication() {
    const [userName, setUserName] = useState("");
    const [PW, setPW] = useState("");
    const handleOnSubmit = async (e) => {
      console.log(userName, PW)
      e.preventDefault();
      try{
         let result = await fetch(
            'http://localhost:8080/api/auth/signin', {
               method: "post",
               body: JSON.stringify({ userName, PW }),
               headers: {
                  'Content-Type': 'application/json'
               },
            })
         
          result = await result.json();
          alert("Đăng nhập thành công");
          
      }
      catch(err) {
         console.error(err);
      }
    }
        //Chỉ định một state
        this.state = {deactivate: "form-wrapper",
                      activate: "form-wrapper is-active" };
    return (
        <div>
            <section className="forms-section">
        <h1 className="section-title">CHào mừng bạn đến với Traveloka*</h1>
        <div className="forms">
            <div className={this.state.activate}>
            <button type="button" className="switcher switcher-login" onClick={() => {
            this.setState({
                deactivate: "form-wrapper",
                activate: "form-wrapper  is-active"
            })
            }}>
                Đăng nhập
                <span className="underline" />
            </button>
            <form className="form form-login">
                <fieldset>
                <legend>Vui lòng điền thông tin.</legend>
                <div className="input-block">
                    <label htmlFor="login-email">E-mail</label>
                    <input id="login-email" type="username" required />
                </div>
                <div className="input-block">
                    <label htmlFor="login-password">Password</label>
                    <input id="login-password" type="password" required />
                </div>
                </fieldset>
                <button type="submit" className="btn-login" onClick="">Login</button>
            </form>
            </div>
            <div className= {this.state.deactivate}>
            <button type="button" className="switcher switcher-signup" onClick={() => {
            this.setState({
                deactivate: "form-wrapper is-active",
                activate: "form-wrapper "
            })
            }}>
                Đăng kí
                <span className="underline" />
            </button>
            <form className="form form-signup">
                <fieldset>
                <legend>Please, enter your email, password and password confirmation for sign up.</legend>
                <div className="input-block">
                    <label htmlFor="signup-username">Username</label>
                    <input id="signup-email" type="text" required />
                </div>
                <div className="input-block">
                    <label htmlFor="signup-email">E-mail</label>
                    <input id="signup-email" type="email" required />
                </div>
                <div className="input-block">
                    <label htmlFor="signup-password">Password</label>
                    <input id="signup-password" type="password" required />
                </div>
                </fieldset>
                <button type="submit" className="btn-signup">Sign Up</button>
            </form>
            </div>
        </div>
        </section>
        </div>
    )
    }


export default Authentication;
